sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && !g_form.isNewRecord|| newValue == oldValue || newValue == '' ) {
        return;
    }

        var ga = new GlideAjax('GetEmailAddress');
        ga.addParam('sysparm_name', 'getEmail');
        ga.addParam('sysparm_userId', g_form.getValue('u_requested_for'));
        //Type appropriate comment here, and begin script below
        ga.getXML(emailId);

        function emailId(response) {
            var response1 = response.responseXML.documentElement.getAttribute('answer');
            g_form.clearValue('u_email');
            g_form.setValue('u_email', response1);

    }
}
